import React from "react";
import "./App.css";
import Title from "./Title";
import Feature from "./Feature";
import Clock from "./Clock";
import Author from "./Author";
import ClockControl from "./ClockControl";


let countDown;
let countDown2;

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            breakLength: 5,
            sessionLength: 24,
            sessionTime: 1500,
            breakTime: 300,
            timeType: "Session",
            timeState: "stop"
        };

        this.changeColor = React.createRef();
        this.setBreakLength = this.setBreakLength.bind(this);
        this.setSessionLength = this.setSessionLength.bind(this);
        this.time = this.time.bind(this);
        this.countDown = this.countDown.bind(this);
        this.stop = this.stop.bind(this);
        this.reset = this.reset.bind(this);
    }

    setBreakLength(e) {
        if (e.target.value === "-") {
            this.setState({
                breakLength: this.state.sessionLength -= 1,
                breakTime: this.state.sessionTime -= 60
            });
        }
    }

    setSessionLength(e) {
        if (e.target.value == "-") {
            this.setState({
                sessionLength: this.state.sessionLength - 1,
                sessionTime: this.state.sessionTime - 60
            });
        }
        else if (e.target.value == "+") {
            this.setState({
                sessionLength: this.state.sessionLength + 1,
                sessionTime: this.state.sessionTime + 60
            });
        }
    }

    time() {
        let minute = Math.floor(this.state.sessionTime / 60);
        let second = this.state.sessionTime - minute * 60;
        minute = minute < 10 ? "0" + minute : minute;
        second = second < 10 ? "0" + second : second;
        return minute + " : " + second;
    }

    countDown() {
        if (this.state.timeState ==="stop") {
            countDown = setInterval(() => {
                this.setState({
                    sessionTime: this.state.sessionTime -= 1
                });
                if (this.state.sessionTime === 0) {
                    clearInterval(countDown);
                    this.setState({
                        timeType: "Break"
                    });
                }
            }, 1000);
            this.setState({
                timeState: "count down"
            });
        }
    }

    stop() {
        clearInterval(countDown);
        this.setState({
            timeState: "stop"
        });
    }

    reset() {
        this.setState({
            breakLength: 5,
            sessionLength: 25,
            sessionTime: 1500,
            breakTime: 300,
            timeType: "Session",
            timeState: "stop",
            timeColor: {color: "white"}
        });
        clearInterval(countDown);
    }

    render() {
        return (
            <div className="container cont">
                <Title />
                <Feature
                breakLength={this.state.breakLength}
                sessionLength={this.state.sessionLength}
                breakChange={this.setBreakLength}
                sessionChange={this.setSessionLength} />
                <Clock 
                timeType={this.state.timeType}
                sessionTime={this.state.sessionTime}
                time={this.time()} />
                <ClockControl
                countDown={this.countDown}
                stop={this.stop} 
                reset={this.reset} />
                <Author />
            </div>
        );
    }
}

export default App;
