import React from "react";
import "./App.css";
import Title from "./Title";
import Feature from "./Feature";
import Clock from "./Clock";
import Author from "./Author";
import ClockControl from "./ClockControl";


let countDown;

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            breakLength: 5,
            sessionLength: 25,
            time: 61,
            timeType: "Session",
            timeState: "stop",
            timeColor: {color: "white"}
        };

        this.changeColor = React.createRef();
        this.setBreakLength = this.setBreakLength.bind(this);
        this.setSessionLength = this.setSessionLength.bind(this);
        this.timeColor = this.timeColor.bind(this);
        this.time = this.time.bind(this);
        this.countDown = this.countDown.bind(this);
        this.stop = this.stop.bind(this);
        this.reset = this.reset.bind(this);
    }

    setBreakLength() {

    }

    setSessionLength() {

    }

    timeColor() {
        if (this.state.time < 61) {
            this.setState({
                timeColor: {color: "#a50d0d"}
            });
        }
        else if (this.state.time > 61) {
            this.setState({
                timeColor: {color: "white"}
            });
        }
    }

    time() {
        let minute = Math.floor(this.state.time / 60);
        let second = this.state.time - minute * 60;
        minute = minute < 10 ? "0" + minute : minute;
        second = second < 10 ? "0" + second : second;
        return minute + " : " + second;
    }

    countDown() {
        if (this.state.timeState ==="stop") {
            countDown = setInterval(() => {
                this.setState({
                    time: this.state.time -= 1
                });
                if (this.state.time === 0) {
                    clearInterval(countDown);
                }
            }, 1000);
            this.setState({
                timeState: "count down"
            });
        }
    }

    stop() {
        clearInterval(countDown);
        this.setState({
            timeState: "stop"
        });
    }

    reset() {
        this.setState({
            breakLength: 5,
            sessionLength: 25,
            time: 61,
            timeType: "Session",
            timeState: "stop",
            timeColor: {color: "white"}
        });
        clearInterval(countDown);
    }

    render() {
        return (
            <div className="container cont">
                <Title />
                <Feature
                breakLength={this.state.breakLength}
                sessionLength={this.state.sessionLength}
                breakChange={this.setBreakLength}
                sessionChange={this.setSessionLength} />
                <Clock 
                timeType={this.state.timeType}
                timeColor={this.state.timeColor}
                time={this.time()} />
                <ClockControl
                countDown={this.countDown}
                stop={this.stop} 
                reset={this.reset} />
                <Author />
            </div>
        );
    }
}

export default App;
