import React from "react";
import "./App.css";

const aboveOneMin = {
    color: "white"
};

const underOneMin = {
    color: "red"
}

class Clock extends React.Component {
    constructor(props) {
        super(props);
        this.changeColor = React.createRef();
    }

    render() {
        return (
            <div className="clock mt-5 p-5 text-center border border-dark border-5 rounded-pill" ref={this.changeColor}>
                <div className="session">{this.props.timeType}</div>
                <div className="fw-bolder time">{this.props.time}</div>
            </div>
        );
    }
}

export default Clock;
